/* menu bar */
const hamburger = document.querySelector(".c-menu__burger");
const navList = document.querySelector(".c-menu__list");
const navItem = document.querySelectorAll(".c-menu__item");

hamburger.addEventListener("click", () => {
  navList.classList.toggle("c-menu__list--open");
  hamburger.classList.toggle("c-menu__burger--close")
  navItem.forEach(link => {
    link.classList.toggle("c-menu__item--fade");
  });
});

/* menu sticky */
$(document).scroll(function() {

  const scrollValue = $(this).scrollTop();
  const windowWidth = $(window).width();

  let header = $('.l-header');

  if (scrollValue > 150 && windowWidth > 1024) {
    header.addClass('l-header--sticky');
} else {
    header.removeClass('l-header--sticky');
}
})