/*scroll navigation link JQ */
$(".c-menu__link").on("click", function() {
  const goToSection = $(this).attr("href");

  $("body, html").animate(
    {
      scrollTop: $(goToSection).offset().top - $(window).height() / 5
    },
    "3000"
  );
});

